function format(time) {   
    // Hours, minutes and seconds
    var hrs = ~~(time / 3600);
    var mins = ~~((time % 3600) / 60);

    // Output like "1:01" or "4:03" or "123:03"
    var ret = "";
    if (hrs > 0) {
        ret += "" + hrs + " hr "; 
        if (mins > 0) {
            ret += (mins < 10 ? "0" : "") + mins ;
        }
    } else {
        if (mins > 0) {
            ret = (mins < 10 ? "0" : "") + mins;
        }
    }
    return ret;
}

