        msg.seedTrayState[item]["hexColor"]= "green";
        msg.seedTrayState[item]["icon"] = "http://raspberrypi.local:1880/germinated-state.png";

        let daysElapsedSinceSeedDate = Math.abs((new Date().getTime() - Date.parse(msg.seedTrayState[item]["seedPlantedDate"])) / (1000 * 60 * 60 * 24));
