        msg.seedTrayState[item]["progress"]= Math.round(daysElapsedSinceSeedDate / msg.lettuceDaysForTransplant * 100);

        if (msg.seedTrayState[item]["progress"] > 100) {
            msg.seedTrayState[item]["progress"] = 100;
        }
    } 

    // Germinated 
    else if (Date.parse(msg.seedTrayState[item]["germinationDate"]) <= new Date().getTime()
            && new Date().getTime() > Date.parse(msg.seedTrayState[item]["seedPlantedDate"])) {

        msg.seedTrayState[item]["hexColor"]= "green";
        msg.seedTrayState[item]["icon"] = "http://raspberrypi.local:1880/germinating-state.png";

        let daysElapsedSinceSeedDate = Math.abs((new Date().getTime() - Date.parse(msg.seedTrayState[item]["seedPlantedDate"])) / (1000 * 60 * 60 * 24));
        msg.seedTrayState[item]["progress"]= Math.round(daysElapsedSinceSeedDate / msg.lettuceDaysForGermination * 100);


        if (msg.seedTrayState[item]["progress"] > 100) {
            msg.seedTrayState[item]["progress"] = 100;
        }

    } 

    // Seed state
    else if (msg.seedTrayState[item]["seedPlantedDate"] && 
            (new Date(Date.parse(msg.seedTrayState[item]["seedPlantedDate"]))).isToday()) {

        msg.seedTrayState[item]["hexColor"]= "green";
        msg.seedTrayState[item]["icon"] = "http://raspberrypi.local:1880/seed_state.png";

        let daysElapsedSinceSeedDate = Math.abs((new Date().getTime() - Date.parse(msg.seedTrayState[item]["seedPlantedDate"])) / (1000 * 60 * 60 * 24));
        msg.seedTrayState[item]["progress"]= Math.round(daysElapsedSinceSeedDate / msg.lettuceDaysForGermination * 100);


