/*

Required characteristics:
    Active
        INT	0, 1 (inactive, active)
    CurrentHeaterCoolerState
        INT	0,1,2,3 (inactive, idle, heating, cooling)
    TargetHeaterCoolerState
        INT	0,1,2 (auto, heat, cool)
    CurrentTemperature
        FLOAT	\-100 to 500

Optional characteristics:
    LockPhysicalControls
    Name
    RotationSpeed
        FLOAT	0-100
    SwingMode
    HeatingThresholdTemperature
    TemperatureDisplayUnits
    CoolingThresholdTemperature

*/

/* https://github.com/OpenWonderLabs/SwitchBotAPI

Air Conditioner	
command	setAll	
parameter {temperature},{mode},{fan speed},{power state} e.g. 26,1,3,on	the unit of temperature is in celsius;
  modes include 1 (auto), 2 (cool), 3 (dry), 4 (fan), 5 (heat);
  fan speed includes 1 (auto), 2 (low), 3 (medium), 4 (high);
  power state includes on and off

*/

var parameter;

if ('Active' in msg.payload) {

    flow.set("LivingRoom.AC.Active", msg.payload.Active);

    parameter = (msg.payload.Active == 1) ? "turnOn" : "turnOff";
    msg.payload = {"command":parameter,"parameter":"default","commandType":"command"};

} else if ('CurrentTemperature' in msg.payload) {

    flow.set("LivingRoom.CurrentTemperature", msg.payload.CurrentTemperature);

} else if ('HeatingThresholdTemperature' in msg.payload) {

    flow.set("LivingRoom.AC.HeatingThresholdTemperature", msg.payload.HeatingThresholdTemperature);
    if (flow.get("LivingRoom.AC.Active") == 1) {
        parameter = flow.get("LivingRoom.AC.HeatingThresholdTemperature") + "," + 5 + "," + 3 + ",on";
        msg.payload = {"command":"setAll","parameter":parameter,"commandType":"command"};
    }


} else if ('CoolingThresholdTemperature' in msg.payload) {

    flow.set("LivingRoom.AC.CoolingThresholdTemperature", msg.payload.CoolingThresholdTemperature);

    if (flow.get("LivingRoom.AC.Active") == 1) {
        parameter = flow.get("LivingRoom.AC.CoolingThresholdTemperature") + "," + 2 + "," + 3 + ",on";
        msg.payload = {"command":"setAll","parameter":parameter,"commandType":"command"};
    }

} else if ('CurrentHeaterCoolerState' in msg.payload) {

    // CurrentHeaterCoolerState
    //INT	0,1,2,3 (inactive, idle, heating, cooling)
    flow.set("LivingRoom.AC.CurrentHeaterCoolerState", msg.payload.CurrentHeaterCoolerState);


} else if ('TargetHeaterCoolerState' in msg.payload) {

    //TargetHeaterCoolerState
    //    INT	0,1,2 (auto, heat, cool)
    flow.set("LivingRoom.AC.TargetHeaterCoolerState", msg.payload.TargetHeaterCoolerState);

    // SwtichBOT modes include 1 (auto), 2 (cool), 3 (dry), 4 (fan), 5 (heat);
    let mode; 
    if (msg.payload.TargetHeaterCoolerState == 0) {
        mode = 1;
    } else if (msg.payload.TargetHeaterCoolerState == 1) {
        mode = 5;
    } else {
        mode = 0;
    }

    parameter = flow.get("LivingRoom.AC.HeatingThresholdTemperature") + "," + mode + "," + 3 + ",on";
    msg.payload = {"command":"setAll","parameter":parameter,"commandType":"command"};

} else if ('RotationSpeed' in msg.payload) {

    // TODO manage the rotation

    flow.set("LivingRoom.AC.RotationSpeed", msg.payload.RotationSpeed);

    let mode, targetTemperature; 
    var currentState = flow.get("LivingRoom.AC.TargetHeaterCoolerState");
    if (msg.payload.TargetHeaterCoolerState == 0) {
        mode = 1;
        temperature = flow.get("LivingRoom.AC.HeatingThresholdTemperature");
    } else if (msg.payload.TargetHeaterCoolerState == 1) {
        mode = 5;
        temperature = flow.get("LivingRoom.AC.CoolingThresholdTemperature");
    } else {
        mode = 0;
    }

    // fan speed includes 1 (auto), 2 (low), 3 (medium), 4 (high);
