         rotationSpeed = 3;
    } else if (msg.payload.RotationSpeed > 60) {
         rotationSpeed = 4;
    }

    parameter = flow.get("LivingRoom.AC.HeatingThresholdTemperature") + "," + flow.get("LivingRoom.AC.TargetHeaterCoolerState") + "," + rotationSpeed + ",on";
    msg.payload = {"command":"setAll","parameter":parameter,"commandType":"command"};

} 


