var room = msg.topic;

if ('On' in msg.payload) {
    msg.payload= {
        "set": msg.payload.On,
        "operation": "SET",
        "dps": 20
    };
    return msg;

} else {

    if ('Brightness' in msg.payload) {
        flow.set(room + ".LEDStrip.Brightness", msg.payload.Brightness);
    } else if ('Hue' in msg.payload) {
        flow.set(room + ".LEDStrip.Hue", msg.payload.Hue);
    } else if ('Saturation' in msg.payload) {
        flow.set(room + ".LEDStrip.Saturation", msg.payload.Saturation);
    } else if ('ColorTemperature' in msg.payload) {
        flow.set(room + "LEDStrip.ColorTemperature", msg.payload.ColorTemperature);
    }

    var hue = flow.get(room + ".LEDStrip.Hue");
    var brightness = flow.get(room + ".LEDStrip.Brightness") || 100;
    var saturation = flow.get(room + ".LEDStrip.Saturation");

    // If all HSV values exist
    if (hue && brightness & saturation) {

        //  # h:0-360,s:0-1000,v:0-1000|hsv|
        var hexvalue = "";
        var hsvarray = [hue, saturation * 10, brightness * 10];

        for (var value in hsvarray) {

            var temp = hsvarray[value].toString(16).replace("0x", "");
            while (temp.length < 4) {
                temp = "0" + temp;
            }
            hexvalue = hexvalue + temp;
        }

        msg.payload= {
            "set": hexvalue,
            "operation": "SET",
            "dps": 24
             //h:0-360,s:0-1000,v:0-1000	hsv
        };

        msg.topic = msg.topic;
        return msg;

    }

