function timeConvert(UNIX_timestamp, offset){
  var a = new Date(UNIX_timestamp * 1000 + offset*1000);
  var hour = a.getHours();
  var suffix = " am";
  if (hour >= 12) {
    hour = hour - 12;
    suffix = " pm";
  }
  if (hour === 0) {
    hour = 12;
  }
  var min = a.getMinutes();
  if (min < 10) {min = "0" + min;}
  return hour + ':' + min + suffix;
}

var degreesToCardinal = function(deg){
